package functionPkg;

public class StaticClass {

	int staticVar1 = 10;   ///Global variable
	static int staticVar = 10;   //globalsyso/static/class variable
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(staticVar);

	}
	public void add()
	{
		System.out.println(staticVar);
	}
	
	//Static class methods can be accessible inside or outside the class
	

}
